import React, { useState } from "react";
import { View, Text, TextInput, TouchableOpacity, Alert } from "react-native";

export default function ETPWallet() {
  const [etp, setEtp] = useState(0);
  const [amount, setAmount] = useState("");
  const rate = 150;

  const toETP = () => {
    const val = parseFloat(amount) / rate;
    setEtp(etp + val);
    Alert.alert("Converted", `${amount} ETB → ${val.toFixed(2)} ETP`);
  };

  const toETB = () => {
    const val = parseFloat(amount) * rate;
    setEtp(etp - parseFloat(amount));
    Alert.alert("Converted", `${amount} ETP → ${val.toFixed(2)} ETB`);
  };

  return (
    <View style={{ flex: 1, padding: 20, backgroundColor: "#f9f9f9" }}>
      <Text style={{ fontSize: 22, fontWeight: "bold", textAlign: "center", color: "#007bff" }}>ETP Wallet</Text>
      <Text style={{ textAlign: "center", marginVertical: 10 }}>Balance: {etp.toFixed(2)} ETP (≈ {(etp * rate).toFixed(2)} ETB)</Text>

      <TextInput
        placeholder="Amount"
        value={amount}
        keyboardType="numeric"
        onChangeText={setAmount}
        style={{ borderWidth: 1, borderColor: "#ccc", padding: 10, borderRadius: 10, marginVertical: 10 }}
      />

      <TouchableOpacity onPress={toETP} style={{ backgroundColor: "#007bff", padding: 15, borderRadius: 10, marginVertical: 5 }}>
        <Text style={{ color: "#fff", textAlign: "center" }}>Convert to ETP</Text>
      </TouchableOpacity>

      <TouchableOpacity onPress={toETB} style={{ backgroundColor: "#28a745", padding: 15, borderRadius: 10, marginVertical: 5 }}>
        <Text style={{ color: "#fff", textAlign: "center" }}>Convert to ETB</Text>
      </TouchableOpacity>
    </View>
  );
}
